import React, { useState } from "react";
import './ExpenseItem.css';
import ExpenseDate from "./ExpenseDate";
import Card from "../../Ui/Card";

function ExpenseItem(props) {

  const [title, setTitle] = useState(props.title)
  const [editTitle, setEditTitle] = useState(true)

  const clickHandler = (newTitle) => {
    setTitle('newTitle!');
    setEditTitle(false)
  };

  return (
    <Card className="expense-item">
      <ExpenseDate date={props.date}/>
      <div className="expense-item__description">
        <h2>{props.title}</h2>
        <div className="expense-item__price">${props.amount}</div>
      </div>
      {editTitle === false && <button onClick={editHandler} className="change-button">Change title</button>}
      {editTitle === true && 
        <form>
          <div className="label-input">
            <label>New title</label>
            <input type="text" className="change-title-input"></input>
          </div>
          <button type="submit" className="change-button" onSubmit={clickHandler}>Change title</button>
        </form>}
    </Card>
  );
}

export default ExpenseItem;
