import React, { useState } from 'react'
import './ExpenseForm.css'

function ExpenseForm(props) {

    const [enteredTitle, setEnteredTitle] = useState('')
    const [enteredAmount, setEnteredAmount] = useState('')
    const [enteredDate, setEnteredDate] = useState('')
    const [isAmountValid, setIsAmountValid] = useState(true)

    const titleChangeHandler = (event) => {
        setEnteredTitle(event.target.value)
    }
    const amountChangeHandler = (event) => {
        setEnteredAmount(event.target.value)
    }

    const dateChangeHandler = (event) => {
        setEnteredDate(event.target.value)
    }

    const submitHandler = (event) => {
        event.preventDefault();

        const expenseData = {
            title: enteredTitle,
            amount: +enteredAmount,
            date: new Date(enteredDate)
        }

        if (expenseData.amount === 0) {
            setIsAmountValid(false)
            return
        }

        console.log(expenseData);

        props.onSaveExpenseData(expenseData);

        setEnteredTitle('');
        setEnteredAmount('');
        setEnteredDate('');
    }

  return (
    <form onSubmit={submitHandler}>
    <div >
        <div className='new-expense__control'>
            <label>Title</label>
            <input type='text' value={enteredTitle} onChange={titleChangeHandler}/>
        </div>
        <div className='new-expense__control'>
            <label>Amount (In numbers) </label>
            <input style={{backgroundColor: !isAmountValid ? 'salmon' : 'white' }} type='number' min="0.01" step="0.01" value={enteredAmount} onChange={amountChangeHandler}/>
        </div>
        <div className='new-expense__control'>
            <label>Date</label>
            <input type='date' min="2023-01-01" max="2025-01-01" value={enteredDate} onChange={dateChangeHandler}/>
        </div>
        <div className='new-expense__actions'>
            <button type="submit">Add Expense</button>
            <button onClick={props.cancel}>Cancel</button>
        </div>
    </div>
    </form>
  )
}

export default ExpenseForm
